		Variable_high_level_operation_* high_level_operation_ =
			new Variable_high_level_operation_();
		Variable_integration_add_object_* integration_add_object_ =
			new Variable_integration_add_object_();
		Variable_integration_declaration_* integration_declaration_ =
			new Variable_integration_declaration_();
		Variable_integration_implementation_* integration_implementation_ =
			new Variable_integration_implementation_();
